mapper.insert:
INSERT /* com.eunah.project.notice.model.dao.NoticeDAO#insertNotice() */
  INTO TBL_NOTICE
(
  NOTICE_TITLE
, NOTICE_BODY
, NOTICE_WRITER_MEMBER_NO
)
VALUES
(
  #{ title }
, #{ body }
, #{ writerMemberNo }
)

mapper.update:
UPDATE /* com.eunah.project.notice.model.dao.NoticeDAO#incrementNoticeCount() */
	   TBL_NOTICE
   SET NOTICE_COUNT = (SELECT  A.COUNT 
 						 FROM  (
 						 		SELECT NOTICE_COUNT COUNT 
						 		  FROM TBL_NOTICE 
						 		 WHERE notice_no = #{ no }
 						 		) A
                      ) + 1
 WHERE NOTICE_NO = #{ no }
