mapper.insert:
INSERT INTO EMPLOYEE (
                        EMP_ID
                        ,EMP_NAME
                        ,EMP_NO
                        ,EMAIL
                        ,PHONE
                        ,DEPT_CODE
                        ,JOB_CODE
                        ,SAL_LEVEL
                        ,SALARY
                        ,BONUS
                        ,MANAGER_ID
                        ,HIRE_DATE
                        ,ENT_DATE
                        ,ENT_YN
                    )
VALUES (
        #{ empId }
        ,#{ empName }
        ,#{ empNo }
        ,#{ email }
        ,#{ phone }
        ,#{ deptCode }
        ,#{ jobCode }
        ,#{ salLevel }
        ,#{ salary }
        ,#{ bonus }
        ,#{ managerId }
        ,#{ hireDate }
        ,null
        ,'N'
       )

mapper.update:
UPDATE EMPLOYEE
   SET EMP_NAME = #{ empName }
 WHERE EMP_ID = #{ empId }

mapper.delete:
DELETE
  FROM EMPLOYEE
 WHERE EMP_ID = #{ empId }
