mapper.insert:
INSERT /* com.ohgiraffers.project.board.model.dao.BoardDAO#insertBoard() */
  INTO TBL_BOARD
(
  BOARD_TYPE
, CATEGORY_CODE
, BOARD_TITLE
, BOARD_BODY
, BOARD_WRITER_MEMBER_NO
)
VALUES
(
  1
, #{ categoryCode }
, #{ title }
, #{ body }
, #{ writerMemberNo }
)

INSERT /* com.ohgiraffers.project.board.model.dao.BoardDAO#insertAttachment() */
  INTO TBL_ATTACHMENT
(
  REF_BOARD_NO
, ORIGINAL_NAME
, SAVED_NAME
, SAVE_PATH
, FILE_TYPE
, THUMBNAIL_PATH
)
VALUES 
(
  #{ refBoardNo }
, #{ originalName }
, #{ savedName }
, #{ savePath }
, #{ fileType }
, #{ thumbnailPath }
)

mapper.update:
UPDATE /* com.ohgiraffers.project.board.model.dao.BoardDAO#incrementBoardCount() */
       TBL_BOARD
   SET BOARD_COUNT = (SELECT A.COUNT 
   						FROM (
   								SELECT BOARD_COUNT COUNT
                        		  FROM TBL_BOARD 
                       			 WHERE BOARD_NO = #{ no }
                     		  ) A
                       ) + 1
 WHERE BOARD_NO = #{ no }
